const ServiceCard = ({ title, description, image, Svg }) => {
    const [ref, inView] = useInView({
        threshold: 0.3,
        triggerOnce: true
    });

    const [mouseEntered, setMouseEntered] = useState(false)

    return (
        <motion.div
            ref={ref}
            initial={{ opacity: 0, y: 50 }}
            animate={inView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8 }}
            onMouseEnter={() => setMouseEntered(true)}
            onMouseLeave={() => setMouseEntered(false)}
            whileHover={{ scale: 1.03, cursor: 'pointer' }}
            className="bg-white rounded-4xl  p-8 py-10  shadow-lg hover:shadow-xl transition-shadow duration-300"
        >
            <div className="space-y-4">
                <div className="flex items-center border-b pb-5 text-gray-300 space-x-3">
                    <div className={`${mouseEntered ? 'bg-black' : 'bg-[#5e5eee]'} p-2  transition-all duration-75 rounded-xl`}>
                        {Svg}
                    </div>
                    <h3 className="text-xl font-bold text-gray-800">{title}</h3>
                </div>
                <p className="text-gray-600">{description}</p>
                <div className="rounded-4xl overflow-hidden mt-10">
                    <img src={image} alt={title} className="w-full h-52 object-cover" />
                </div>
            </div>
        </motion.div>
    );
};

